import React, { useState, useEffect } from 'react';

function TypingEffect() {
  const texts = ['前端工程師', '後端工程師', '全端工程師', '遊戲開發者', '3D建模師', '人像攝影師'];
  const [count, setCount] = useState(0);
  const [index, setIndex] = useState(0);
  const [letter, setLetter] = useState('');

  useEffect(() => {
    if (index === 0) {
      setLetter(texts[count].charAt(0));
      setIndex(1);
    }

    if (index > 0 && index <= texts[count].length) {
      const timer = setTimeout(() => {
        setLetter(texts[count].substring(0, index + 1));
        setIndex(index + 1);
      }, 150);

      return () => clearTimeout(timer);
    } else if (index > texts[count].length) {
      setTimeout(() => {
        setIndex(0);
        setCount((prevCount) => (prevCount + 1) % texts.length);
      }, 3000);
    }
  }, [index, texts, count]);

  return <h1 className="note-sans-black fs-1 mt-5 pt-5" id="typing">{letter}</h1>;
}

export default TypingEffect;
