import React, { useState, useEffect } from 'react';

function TypingEffect() {
  const texts = ['前端工程師', '後端工程師', '全端工程師', '遊戲開發者', '3D建模師', '人像攝影師'];
  const [count, setCount] = useState(0);
  const [index, setIndex] = useState(0);
  const [currentText, setCurrentText] = useState('');
  const [letter, setLetter] = useState('');

  useEffect(() => {
    if (count === texts.length) {
      setCount(0);
    }
    const timer = setTimeout(() => {
      setCurrentText(texts[count]);
      setLetter(currentText.slice(0, index + 1));
      if (index === currentText.length - 1) {
        setCount(count + 1);
        setIndex(0);
      } else {
        setIndex(index + 1);
      }
    }, index === currentText.length - 1 ? 3000 : 150);

    return () => clearTimeout(timer);
  }, [letter, texts, count, index, currentText]);

  return <h1 className="note-sans-black fs-1 mt-5 pt-5" id="typing">{letter}</h1>;
}

export default TypingEffect;
