import logo from './logo.svg';
import './App.css';
import React, { useEffect } from 'react';

function Home() {
  useEffect(() => {
    document.title = '魔法商店 | 首頁';
  }, []);
  return (
    <main class="row justify-content-evenly g-0" style="overflow: hidden;">
      <section id="home2" class="d-flex justify-content-center text-white image-container pt-5"
        style="height: 100vh; background-image: url('../static/img/Fp0cxYQagAUt7tq.jfif'); 
      background-repeat: no-repeat; background-size: cover; background-position: center; position: relative; background-attachment: fixed;">

        <div class="d-flex justify-content-center image-overlay" style="position: absolute; top: 0; left: 0; 
        width: 100%; height: 100%; background: rgba(0, 0, 0, 0.5);">

          <div class="d-flex flex-column justify-content-center ms-5 me-5 ps-5 pe-5">
            <br />
            <h1 class="fs-1 mt-5 pt-5" id="typing">UI 設計師</h1>
            <p class="mt-3 fs-6">"歡迎來到創意的交匯點！
              我們是一群熱衷於技術和藝術的專業人士，涵蓋從前端工程師到3D建模師的各個範疇。
              無論是創造引人入勝的網站體驗、開發先進的後端解決方案、
              或是打造下一代遊戲和實現視覺震撼，我們都在這裡，準備將您的夢想轉化為現實。"</p>
            <p class="mt-5"><a href="../about.html" class="button text-dark"
              style="box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);">了解更多</a></p>
          </div>
        </div>
      </section>

      <section class="container col-12 row justify-content-evenly align-content-evenly pt-5 pb-5 gap-5" id="home">

      </section>
    </main>
  );
}

export default Home;
